/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect, useState } from 'react';


function AdminManageUsers() {
    const [users, setUsers] = useState(undefined);


    useEffect(() => {

        fetch('http://192.168.1.29:4000/users/all', {
            method: "GET",
            headers: {
                "Content-type": "application/json;charset=UTF-8",
                authorization: JSON.parse(localStorage.getItem('authorization'))
            },
        })
            .then(res => res.json())
            .then(data => setUsers(data.users))
            .catch(err => console.log(err));
    }, [])


    return (
        <div className="overflow-x-auto w-5/5 lg:w-2/5 rounded shadow-[0_2px_10px] shadow-blackA7 bg-slate-800 border">
            <h1 className='text-center text-2xl text-bold underline text-white'>Tüm Üyeler</h1>
            {users && (
                <>
                    <table className="table table-zebra">
                        {/* head */}
                        <thead>
                            <tr>
                                <th>İsim</th>
                                <th>Email</th>
                                <th>Yetkiler</th>
                                <th>Üye Olduğu kulüpler</th>
                                <th>Kurucu Olduğu Kulüpler</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <h1>
                                    <tr>
                                        <th>{user.name}</th>
                                        <td>{user.email}</td>
                                        <td>{user.permissions.map((p) => <a>{p}</a>)}</td>
                                        <td>{user.participatingClubs.map((p) => <a>{p}</a>)}</td>
                                        <td>{user.ownering.map((o) => <a>{o}</a>)}</td>
                                    </tr>
                                </h1>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    )
}

export default AdminManageUsers
