import ScrollArea from './components/ScrollArea';
import ClubDetail from './components/ClubDetail';
import './App.css';
import { useState, useEffect } from "react";


function App() {


  const [pickedClub, setPickedClub] = useState({
    id: 1,
    name: 'Münazara',
    img: "https://raw.githubusercontent.com/Akiftsc/baalMunazara/master/img/clubLogo.jpg",
    description: "Münazara Kulübü! Bütün karşıt fikirlerinizi doyasıya paylaşacağınız, hitabetinizi geliştireceğiniz, birbirinden farklı insanlar tanıyacağınız ve maçlarda unutulmayacak keyifli anılar biriktireceğiniz bir kulüp arıyorsanız; doğru yerdesiniz. Gelişmelerden haberdar olmak için takipte kalın, en yakın zamanda görüşmek üzere."
  });
  const [register, setRegister] = useState(false);

  useEffect(() => {
    setRegister(false)
  }, [pickedClub])


  return (
    <>
      <div>
        <h1 className='text-5xl text-center font-bold text-white tracking-tighter'>
          <span className='inline text-red-600'>BAAL</span>
          Kulüpler Birliği
        </h1>
      </div>
      <div /* style={{ backgroundColor: "rgb(29, 39, 57)" }} */ className='container mx-auto mt-4 rounded p-5 flex align-center space-between border gap-3 lg:flex-row flex-col '>
        <ScrollArea setPickedClub={setPickedClub} />
        {pickedClub &&
          <>
            <input type="checkbox" id="my_modal_3" className="modal-toggle" />
            <div className="modal" id="my_modal_3">
              <div className="modal-box" style={{ padding: "0", border: "none" }}>
                <button className="btn btn-sm btn-circle btn-ghost absolute right-2 top-2">✕</button>
                <ClubDetail pickedClub={pickedClub} setRegister={setRegister} register={register} />
              </div>
              <label className="modal-backdrop" htmlFor="my_modal_3">Close</label>
            </div>
          </>
        }
      </div>

    </>
  );

}

export default App;
