import React, { useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
const { useParams } = "react-router-dom";
const axios = require('axios');


function ClubDetail() {

    /* let { id } = useParams(); */

    useEffect(() => {
        //Fetch data from API
    }, [])

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };

    return (
        <div className='container-md'>
            <section class="text-white body-font">
                <div class="container px-5 py-24 mx-auto">
                    <div class="flex flex-wrap w-full mb-20">
                        <div class="lg:w-1/2 mb-6 lg:mb-0 flex gap-2 align-center flex-col lg:flex-row">
                            <span className='w-24 rounded-full'>
                                <img className='rounded-full' src='https://res.cloudinary.com/defq71a8p/image/upload/v1690265786/bcvxvwadktvsfrfwomk9.webp' />
                            </span>
                            <h1 class="sm:text-4xl text-2xl font-medium title-font mb-2 text-white text-center grid align-center">
                                Münazara Kulübü
                            </h1>
                        </div>
                        <p class="lg:w-1/2 w-full leading-relaxed text-white">
                            Bülent Akarcalı Anadolu Lisesi öğrencileri gururla takdim eder! Karşınızda, okulumuzun ilk öğrenci kulübü olan Münazara Kulübü! Bütün karşıt fikirlerinizi doyasıya paylaşacağınız, hitabetinizi geliştireceğiniz, birbirinden farklı insanlar tanıyacağınız ve maçlarda unutulmayacak keyifli anılar biriktireceğiniz bir kulüp arıyorsanız; doğru yerdesiniz. Gelişmelerden haberdar olmak için takipte kalın, en yakın zamanda görüşmek üzere.
                        </p>
                    </div>
                    <Swiper
                        pagination={{
                            type: 'progressbar',
                        }}
                        navigation={true}
                        modules={[Pagination, Navigation]}
                        className="mySwiper"
                    >

                        <SwiperSlide>
                            <div class="bg-slate-700 p-6 rounded-lg max-w-xl">
                                <img src="https://i.ibb.co/JdJ0ShV/337532739-6025344700893105-8788764806042147948-n.jpg" />
                            </div>
                        </SwiperSlide>
                    </Swiper>
                </div>
            </section>
        </div>
    )
}

export default ClubDetail
