import ScrollArea from './components/ScrollArea';
import ClubDetail from './components/ClubDetail';
import './App.css';
import { useState, useEffect } from "react";


function App() {


  const [pickedClub, setPickedClub] = useState();
  const [register, setRegister] = useState(false);

  useEffect(() => {
    setRegister(false)
  }, [pickedClub])


  return (
    <>
      <div>
        <h1 className='text-5xl text-center font-bold text-white tracking-tighter'>
          <span className='inline text-red-600'>BAAL</span>
          Kulüpler Birliği
        </h1>
      </div>
      <div /* style={{ backgroundColor: "rgb(29, 39, 57)" }} */ className='container mx-auto mt-4 rounded p-5 flex align-center space-between border gap-3 lg:flex-row flex-col '>
        <ScrollArea setPickedClub={setPickedClub} />
        {pickedClub &&
          <>
            <dialog id="my_modal_3" class="modal">
              <div class="modal-box">
                <button class="btn btn-sm btn-circle btn-ghost absolute right-2 top-2">✕</button>
                <ClubDetail pickedClub={pickedClub} setRegister={setRegister} register={register} />

              </div>
            </dialog>
          </>
        }
      </div>

    </>
  );

}

export default App;
