import React from 'react';
import * as ScrollArea from '@radix-ui/react-scroll-area';




const ScrollAreaDemo = function ({ setPickedClub }) {

    const clubs = [
        {
            id: 1,
            name: 'Münazara',
            img: "https://raw.githubusercontent.com/Akiftsc/baalMunazara/master/img/clubLogo.jpg",
