import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Profile() {
    const [userData, setUserData] = useState(null);
    const [clubData, setClubData] = useState(null);
    const navigate = useNavigate();

    const checkAdmin = async () => {
        const storedToken = JSON.parse(localStorage.getItem('authorization'));
        if (storedToken !== null) {
            try {
                const response = await axios.get(
                    `http://192.168.1.29:4000/checkadmin/${JSON.parse(
                        localStorage.getItem('authorization')
                    )}`
                );
                const data = response.data;

                if (data.isLoggedIn) {
                    const res = await axios.get('http://192.168.1.29:4000/myclubs/' + JSON.parse(localStorage.getItem('authorization')));
                    setUserData(data.user);
                    setClubData(res.data);
                    console.log(clubData);
                } else {
                    navigate('/login');
                }
            } catch (error) {
                navigate('/login');
            }
        } else {
            navigate('/login');
        }
    };

    useEffect(() => {
        checkAdmin();
    }, []);


    return (
        <>
            <h1 className='text-center text-3xl text-white'>Merhaba {userData.name}</h1>
            {clubData && (
                <>
                    <h1>Üyesi olduğun Kulüpler.</h1>
                    {
                        clubData.map((club) => (
                            <p>
                                {club}
                            </p>
                        ))
                    }
                </>
            )}
        </>
    );
}

export default Profile;
