/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect, useState } from 'react';
import axios from "axios";


function AdminManageUsers() {
    const [users, setUsers] = useState(undefined);


    useEffect(() => {

        fetch('http://192.168.1.29:4000/users/all', {
            method: "GET",
            headers: {
                "Content-type": "application/json;charset=UTF-8",
                authorization: JSON.parse(localStorage.getItem('authorization'))
            },
        })
            .then(res => res.json())
            .then(data => setUsers(data.users))
            .catch(err => console.log(err));
    }, []);


    const authHandler = async (e) => {
        const id = e.target.parentElement.getAttribute('data-id');
        console.log("ðŸš€ ~ file: AdminManageUsers.js:27 ~ authHandler ~ id:", id)

        debugger;;
        const b = {
            role: "Admin",
            id: id
        };
        const response = await axios.post(`http://192.168.1.29:4000/user/auth/`, b, {
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                "authorization": JSON.parse(localStorage.getItem("authorization")),
            },
        });
    }


    return (
        <div className="overflow-x-auto w-5/5 lg:w-3/5 rounded shadow-[0_2px_10px] shadow-blackA7 bg-slate-800 border">
            <h1 className='text-center text-2xl text-bold underline text-white'>TÃ¼m Ãœyeler</h1>
            {users && (
                <>
                    <table className="table table-zebra">
                        {/* head */}
                        <thead>
                            <tr className='text-white'>
                                <th>Ä°sim</th>
                                <th>Email</th>
                                <th>Yetkiler</th>
                                <th>Ãœye OlduÄŸu kulÃ¼pler</th>
                                <th>Kurucu OlduÄŸu KulÃ¼pler</th>
                                <th>Yetkilendir</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr className='!bg-slate-800' id={user._id}>
                                    <th>{user.name}</th>
                                    <td>{user.email}</td>
                                    <td>{user.permissions.map((p) => <a>{p} </a>)}</td>
                                    <td>{user.participatingClubs.map((p) => <li>{p.name}</li>)}</td>
                                    <td>{user.ownering.map((o) => <a>{o.name}</a>)}</td>
                                    <td>
                                        <button className="btn btn-sm btn-info my-2 text-xs" onClick={authHandler}>Admin</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    )
}

