import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Profile() {
    const [userData, setUserData] = useState(null);
    const [clubData, setClubData] = useState(null);
    const navigate = useNavigate();

    const checkAdmin = async () => {
        const storedToken = JSON.parse(localStorage.getItem('authorization'));
        if (storedToken !== null) {
            try {
                const response = await axios.get(
                    `http://192.168.1.29:4000/checkadmin/${JSON.parse(
                        localStorage.getItem('authorization')
                    )}`
                );
                const data = response.data;

                if (data.isLoggedIn) {
                    const res = await axios.get('http://192.168.1.29:4000/myclubs/' + JSON.parse(localStorage.getItem('authorization')));
                    setUserData(data.user);
                    console.log(res.data.clubs);
                } else {
                    navigate('/login');
                }
            } catch (error) {
                navigate('/login');
            }
        } else {
            navigate('/login');
        }
    };

    useEffect(() => {
        checkAdmin();
    }, []);

    return (
        <>
            {userData && (
                <div className='text-white'>
                    <h1>Merhaba {userData.name}!</h1>

                </div>
            )}
        </>
    );
}

export default Profile;
