import React from 'react'
import { useState, useEffect } from "react";
import axios from "axios";

function AdminTable() {

    const [clubs, setClubs] = useState([]);

    useEffect(() => {
        fetch('http://localhost:4000/clubs/')
            .then(res => res.json())
            .then(data => setClubs(data))
            .catch(err => console.log(err));
    }, [])


    const removeHandler = async (e) => {
        const id = e.target.parentElement.parentElement.id;
        const response = await axios.get(`http://localhost:4000/club/remove/${id}`, {
            headers: {
                "Accept": 'application/json',
                'Content-Type': 'application/json',
                "authorization": JSON.stringify(localStorage.getItem('authorization'))
            },
        });

        const data = response;
        if (data.data.acknowledged) {
            setClubs(clubs.filter(club => club._id !== id));
        }
    }



    return (
        <div className="overflow-x-auto w-5/5 lg:w-3/5 rounded shadow-[0_2px_10px] shadow-blackA7 bg-slate-800 border">
            <h1 className='text-center text-2xl text-bold underline text-white'>Kulüpler</h1>
            <table className="table">
                {/* head */}
                <thead className='text-white'>
                    <tr className='text-lg'>
                        <th>Resim</th>
                        <th>İsim</th>
                        <th>Açıklama</th>
                    </tr>
                </thead>
                <tbody>
                    {clubs.map((club, key) => (
                        <tr id={club._id} key={key}>
                            <td>
                                <div className="flex items-center space-x-3">
                                    <div className="avatar">
                                        <div className="mask mask-squircle w-12 h-12">
                                            <img src={club.img} alt="" />
                                        </div>
                                    </div>
                                </div>
                            </td>
                            <td>
                                {club.name} Kulübü
                                <br />
                            </td>
                            <td>{club.description.substring(0, 40)}...</td>
                            <th>
                                <button className="btn btn-warning btn-xs">Güncelle</button>
                            </th>
                            <th>
                                <button className="btn btn-error btn-xs" onClick={removeHandler}>Sil</button>
                            </th>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

export default AdminTable
